import {useEffect, useState} from 'react';


//1. useEffect(callback)
//- được gọi callback sau khi re-render
//-gọi callback sau khi component them element vào Dom
//2. useEffect(callback, [])
//- chỉ gọi callback 1 lần sau khi component mounted
//3. useEffect(callback, [deps])
//-callback sẽ đc gọi lại mỗi khi deps thay đổi

//--------------------- đúng với cả 3 tk bên trên
//1. Callback luôn được gọi sau khi component mounted 
//2. cleanup function luôn đc gọi trc khi component unmounted


const tabs = ['posts', 'comments', 'albums']

function Content(){
    const [title, setTitle] = useState('');
    const[type, setType] = useState('posts');
    const [posts, setPosts] = useState([]);
    const[showGoToTop, setShowGoToTop] = useState(false);

    useEffect(()=>{
        // console.log('Mounted ' + title);
        // document.title = title;

        fetch(`https://jsonplaceholder.typicode.com/${type}`)
        .then(res => res.json())
        .then(posts => {
            setPosts(posts)
        });
    }, [type])

    useEffect(() => {
        const handleScroll = () => {
            console.log(windo);
            // if(window.scrollY >= 200){
            //     //show scroll
            //     setShowGoToTop(true);
            // }else{
            //     //hide button scroll
            //     setShowGoToTop(false);
            // }
            // setShowGoToTop(window.scrollY > 200);
        } 
        window.addEventListener('scroll', handleScroll);

        //cleanup function
        // return () => {
        //     console.log('Unmounting');
        // }
    },[])

    return(
        <div>
            {tabs.map(tab=>(
                <button 
                key={tab}
                style={type === tab ? {color:'#fff', backgroundColor:'#333'}:{}}
                onClick={() => setType(tab)}
                >{tab}</button>
            ))}
            <input value={title} onChange={e=>setTitle(e.target.value)}/>

            <ul>
                {posts.map(post =>(
                    <li key={post.id}>{post.title || post.name}</li>
                ))}
            </ul>
            {showGoToTop && (
                <button style={{
                    position:'fixed',
                    bottom:20,
                    right:20,
                }}>
                    Top
                </button>
            )}
        </div>
     
    )
}

export default Content;
